package DataTypes_Operators;

public class Literals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=134;
		int b=0b11;
		int c=0x11;
		int d=01001101;
		
	    System.out.println(a + " "+ b+ " "+ c+" "+ d);
	    
	    float e=12.34f;
	    float f=12.34554f;
	    System.out.println(e + " " + f);
		
	    int g=12_343_434;
	    long h=3432_4534_34345_543656L;
	    double i= 32434_34.343f;
	    long j=0001_1100_1010_0011L;
	    int k=0X12_3EE_2FA;

	    System.out.println(g + " " + h + " "+ i+ " "+j +" "+k);

	}

}
